import React, { useRef, useState} from "react";
import {Link, Outlet} from "react-router-dom";
import TextField from "@mui/material/TextField";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputAdornment from "@mui/material/InputAdornment";
import InputLabel from "@mui/material/InputLabel";
import FormControl from "@mui/material/FormControl";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import SaveIcon from "@mui/icons-material/Save";
import SendIcon from "@mui/icons-material/Send";
import emailjs from "@emailjs/browser";
import "../App.css"
import "./style.css"

var amountValue;
var taxamount;
var total;


function MainContent() {

  const [compName, setCompName] = useState("");
  const [compAddress, setCompAddress] = useState("");
  const [compNumber, setCompNumber] = useState("");
  const [compinvoiceId, setInvoiceID] = useState("");
  const [compInvoiceDate, setCompInvoiceDate] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [customerAddress, setCustomerAddress] = useState("");
  const [customerNumber, setCustomerNumber] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [compdueDate, setDueDate] = useState("");
  const [paymentDate, setPaymentDate] = useState("");
  const [tableProductDate, setTableProductDate] = useState("");
  const [Description, setDescription] = useState("");
  const [quntity, setQuntity] = useState("");
  const [rate, setRate] = useState("");
  const [amount, setAmounts] = useState("");
  const [tax, setTax] = useState("");
  const [taxAmount, setTaxAmount] = useState("");
  const [totalAmount, setTotalAmount] = useState("");
  const [tablePaymentId, setTablePaymentId] = useState("");
  const [tableBankName, setTableBankName] = useState("");
  const [tableAccNumber, setTableAccNumber] = useState("");
  const [tableifscCode, setTableifscCode] = useState("");
  
  const setAmount = () => {
    var qunt = document.getElementById("quntity").value;
    var ra = document.getElementById("rate").value;
    setQuntity(qunt);
    setRate(ra);
    amountValue = qunt * ra;
    setAmounts(amountValue);
    document.getElementById("amount").value = amountValue;
    var taxper = document.getElementById('tax').value;
    var taxAmount = (amountValue / 100) * taxper;
    setTaxAmount(taxAmount);
    setTax(taxper);
    document.getElementById('taxamount').value = taxamount;
    total = amountValue + taxAmount;
    setTotalAmount(total);

    document.getElementById('totalamount').value = total;
    console.log(taxAmount); 
  };

  const form = useRef();

  const sendEmail = async (e) => {
    e.preventDefault();
    let result = await fetch(
    'http://localhost:5000/create', {
        method: "post",
        body: JSON.stringify({ 
          compName,
          compAddress,
          compNumber,
          _id:compinvoiceId,
          compInvoiceDate,
          customerName,
          customerAddress,
          customerNumber,
          customerEmail,
          compdueDate,
          paymentDate,
          tableProductDate,
          Description,
          quntity,
          rate,
          amount,
          tax:tax,
          taxamount:taxAmount,
          totalAmount,
          tablePaymentId,
          tableBankName,
          tableAccNumber,
          tableifscCode
         }),
        headers: {
            'Content-Type': 'application/json'
        }
    })
    await result.json();
    console.log(result);
    window.alert("Data as been added")
  
    // emailjs
    //   .sendForm(
    //     "service_hbv67p9",
    //     "template_68quqgd",
    //     form.current,
    //     "tQ1OsJ2Hygd0Atlz0"
    //   )
    //   .then(
    //     (result) => {
    //       console.log(result.text);
    //     },
    //     (error) => {
    //       console.log(error.text);
    //     }
    //   );
    //   alert("Mail as sent to Customer Email")
  };


  

  
  return (
    <div id="mainWebPage">
    <div className="webPage" >
      <form
        ref={form}
        onSubmit={sendEmail}
        style={{ marginLeft: "2.5%", marginRight: "2%" }}
      >
        <img src="https://vyaparwebsiteimages.vypcdn.in/logo1.svg" alt="..." style={{height:"100px", width:"400px",margin:"0", textAlign:"left" }}/>
       
        <hr style={{ backgroundColor: "blue", width: "100%", height: "5px" }} />
        <h2>Company Details :</h2>
        <div style={{ marginBottom: "20px" }}>
          <TextField
            required
            id="companyName"
            type="text"
            label="Name"
            onChange={(e) => setCompName(e.target.value)}
            style={{ marginRight: "40px", marginLeft: "30px" }}
            name="company_name"
          />
          <TextField
            required
            id="companyAddress"
            type="text"
            label="Address"
            onChange={(e) => setCompAddress(e.target.value)}
            style={{ marginRight: "40px" }}
            name="company_address"
          />
          <TextField
            required
            id="companyNumber"
            type="tel"
            label="Phone Number"
            onChange={(e) => setCompNumber(e.target.value)}
            style={{ marginRight: "40px" }}
            name="company_number"
          />
          <TextField
            required
            id="invoiceId"
            type="tel"
            label="Invoice ID"
            onChange={(e) => setInvoiceID(e.target.value)}
            style={{ marginRight: "40px" }}
            name="invoiceId"
          />
          <TextField
            required
            id="invoiceDate"
              label="Invoice Date"
              className="invoicedate"
              name="invoiceDate"
              onChange={(e) => setCompInvoiceDate(e.target.value)}
            style={{ marginRight: "40px" }}
          />
        </div>
        <hr style={{ width: "100%", height: "0", borderStyle: "dotted" }} />
        <h2>Customer Details:</h2>
        <div style={{ marginBottom: "20px" }}>
          <TextField
            required
            id="customerName"
            type="text"
            label="Name"
            onChange={(e) => setCustomerName(e.target.value)}
            style={{ marginRight: "20px", marginLeft: "30px", width: "200px" }}
            name="customer_name"
          />
          <TextField
            required
            id="customerAddress"
            type="text"
            label="Address"
            onChange={(e) => setCustomerAddress(e.target.value)}
            style={{ marginRight: "20px", width: "200px" }}
            name="customer_address"
          />
          <TextField
            required
            id="customerNumber"
            type="tel"
            label="Phone Number"
            onChange={(e) => setCustomerNumber(e.target.value)}
            style={{ marginRight: "20px", width: "200px" }}
            name="customer_number"
          />
          <TextField
            required
            id="customerEmail"
            type="email"
            label="Email Address"
            onChange={(e) => setCustomerEmail(e.target.value)}
            style={{ marginRight: "20px", width: "200px" }}
            name="emailAddress"
          /> 
          <TextField
            required
            id="paymentDate"
            label="Payment Date"
            name="paymentDate"
            onChange={(e) => setPaymentDate(e.target.value)}
            style={{ marginRight: "20px", width: "200px" }}
          /> 
          <TextField
            required
            id="dueDate"
            label="Due Date"
            name="dueDate"
            onChange={(e) => setDueDate(e.target.value)}
            style={{ marginRight: "20px", width: "200px" }}
            
          />
        </div>
        <hr style={{ width: "100%", height: "0", borderStyle: "dotted" }} />
        <h2>Product Details:</h2>
        <div style={{ marginBottom: "20px" }}>
          <TextField
            required
            id="tableDate"
            label="Delivered Date"
            name="completedDate"
            onChange={(e) => setTableProductDate(e.target.value)}
            sx={{ marginRight: "30px", marginLeft: "30px", padding: "0", width:"160px" }}
          />
          <TextField
            required
            id="productName"
            type="text"
            label="Product/Description"
            onChange={(e) => setDescription(e.target.value)}
            style={{ marginRight: "30px", width: "200px" }}
            name="description"
          />
          <TextField
            required
            id="quntity"
            type="number"
            label="Quntity"
            onChange={setAmount}
            style={{ marginRight: "30px", width: "100px" }}
            name="quntity"
            defaultValue="1"
          />
          <FormControl style={{ marginRight: "30px", width: "120px" }}>
            <InputLabel htmlFor="outlined-adornment-amount">Rate</InputLabel>
            <OutlinedInput
              id="rate"
              startAdornment={
                <InputAdornment position="start">₹</InputAdornment>
              }
              label="Rate"
              name="rate"
              onChange={ setAmount}
            />
          </FormControl>
          <FormControl style={{ marginRight: "30px", width: "130px" }}>
            <InputLabel htmlFor="outlined-disabled">Amount</InputLabel>
            <OutlinedInput
              readOnly
              id="amount"
              startAdornment={
                <InputAdornment position="start">₹</InputAdornment>
              }
              label="Amount"
              type="tel"
              name="amount"
              value={amountValue}
            />
          </FormControl>
          <FormControl style={{ marginRight: "30px", width: "100px" }}>
            <InputLabel htmlFor="outlined-disabled">Tax</InputLabel>
            <OutlinedInput
              required
              id="tax"
              endAdornment={
                <InputAdornment position="end">%</InputAdornment>
              }
              label="Tax"
              type="tel"
              name="tax"
              onChange={setAmount}
            />
          </FormControl>
          <FormControl style={{ marginRight: "30px", width: "120px" }}>
            <InputLabel htmlFor="outlined-disabled">Tax Amount</InputLabel>
            <OutlinedInput
              readOnly
              id="taxamount"
              startAdornment={
                <InputAdornment position="start">₹</InputAdornment>
              }
              label="Tax Amount"
              type="tel"
              name="taxAmount"
              value={taxamount}
            />
          </FormControl>
          <FormControl style={{ marginRight: "30px", width: "140px" }}>
            <InputLabel htmlFor="outlined-disabled">Total Amount</InputLabel>
            <OutlinedInput
              readOnly
              id="totalamount"
              startAdornment={
                <InputAdornment position="start">₹</InputAdornment>
              }
              label="Total Amount"
              type="tel"
              name="totalAmount"
              value={total}
            />
          </FormControl>
        </div>
        <hr style={{ width: "100%", height: "0", borderStyle: "dotted" }} />
        <h2>Bank & Payment Details:</h2>
        <div id="bottomDiv">
          <TextField
            required
            id="paymentId"
            type="text"
            label="Payment Id"
            onChange={(e) => setTablePaymentId(e.target.value)}
            style={{ marginRight: "30px", marginLeft: "30px" }}
            name="paymentId"
          />
          <TextField
            required
            id="bankName"
            type="text"
            label="Bank Name"
            onChange={(e) => setTableBankName(e.target.value)}
            style={{ marginRight: "30px" }}
            name="bankName"
          />
          <TextField
            required
            id="accountNumber"
            type="text"
            label="Account Number"
            onChange={(e) => setTableAccNumber(e.target.value)}
            style={{ marginRight: "30px" }}
            name="accountNumber"
          />
          <TextField
            required
            id="ifscCode"
            type="text"
            label="IFSC Code"
            onChange={(e) => setTableifscCode(e.target.value)}
            style={{ marginRight: "30px" }}
            name="ifscCode"
          />
          <Stack
            sx={{
              display: "inline-block",
              width: "150px",
              marginRight: "10px",
              marginTop: "5px",
            }}
          >
            <Link to="./invoice.js">
              <Button
                variant="contained"
                href="./invoice.js"
                style={{ backgroundColor: "rgb(163 230 53)" }}
                startIcon={<SaveIcon />}
              >
                Create
              </Button>
              </Link>
          </Stack>

          <Stack
            sx={{ display: "inline-block", width: "150px", marginTop: "5px" }}
          >
            <Button variant="contained" type="submit" endIcon={<SendIcon />}>
              Send Mail
            </Button>
          </Stack>
        </div>
      </form>
    </div>
    </div>
  );
}

export default MainContent;






