import React,{useState, useEffect} from 'react'

import { Link} from "react-router-dom";

export default function ForgotPassword() {

    const [email, setEmail] = useState("");
    const [oldPpassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [conformPassword, setConformPassword] = useState("");

    const [dataPassowrd, setDataPassword] = useState("");

    useEffect(() =>{
        try{
          fetch("http://localhost:5000/user/" + email, { method: "get" })
            .then((res) => res.json())
            .then((da) => {
              setDataPassword(da.password);
            });
          }catch(e){
            console.log(e);
        };
      })

      const handeleSubmit = async (e) =>{
        e.preventDefault();

        if(dataPassowrd === oldPpassword && newPassword === conformPassword){
            try{
            await fetch("http://localhost:5000/uppassword/" + email,{
                method:"put",
                body: JSON.stringify({ 
                    password:newPassword
                }),
                headers: {
                    'Content-Type': 'application/json'
                }

        }).then((res) => res.json())
        .then(
            alert("Password Changed Successfully")
        )
        }
        catch(e){
            console.log(e);
        }

    }
    else{
        alert("Password is incorrect")
    }
      }
  return (
    <div className="loginBody">
      <form onSubmit={handeleSubmit}>
        <div className="MainContainer">
        <img src="https://vyaparwebsiteimages.vypcdn.in/logo1.svg" alt="..."/>
            <h2 className="WelcomeText"> Change User Password</h2>
           
            <div className="InputContainer">
                <input type="email" className="inputs" placeholder="Email" onChange={e => setEmail(e.target.value)} required/>  
                <input type="text" className="inputs" placeholder="Old Password" onChange={e => setOldPassword(e.target.value)} required/>
                <input type="text" className="inputs" placeholder="New Password" onChange={e => setNewPassword(e.target.value)} required/>
                <input type="text" className="inputs" placeholder="Conform Password" onChange={e => setConformPassword(e.target.value)} required/>
            </div>
            <div className="ButtonContainer">
                
                <button className="buttons"><Link to="/"><h3 style={{color:"white"}}>Login</h3></Link></button>
                <button className="loginbuttons" type="submit"><h3 style={{color:"white"}}>Change Password</h3></button>
            </div>         
        </div>
      </form>
    </div>
  )
}
