import React, { useEffect, useState } from "react";
import "./login.css";
import { Link, useNavigate } from "react-router-dom";

export default function Login() {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState("");

  const [dataEmail, setDataEmail] = useState("");
  const [dataPassword, setDataPassword] = useState("");
  const [dataUser, setDataUser] = useState("");

  const [dataUserEmail, setDataUserEmail] = useState("");
  const [dataUserPassword, setDataUserPassword] = useState("");
  const [dataUserUser, setDataUserUser] = useState("");

  const hestory = useNavigate();

  useEffect(() =>{
    try{
      fetch("http://localhost:5000/user/" + email, { method: "get" })
        .then((res) => res.json())
        .then((da) => {
          setDataUserEmail(da.email);
          setDataUserPassword(da.password);
          setDataUserUser(da.user);
        });
      }catch(e){
        console.log(e);
    };
    try{
      fetch("http://localhost:5000/admin/" + email, { method: "get" })
        .then((res) => res.json())
        .then((da) => {
          setDataEmail(da.email);
          setDataPassword(da.password);
          setDataUser(da.user);
        });
      }catch(e){
        console.log(e);
    };
  })

  const handelevent = () => {    

  if(email === dataEmail && password === dataPassword && user === dataUser){
    hestory("./Home")
  }
  else if(email === dataUserEmail && password === dataUserPassword && user === dataUserUser){
    hestory("./UserHome")
  }
  else{
    window.alert("invalied email or password")
  }
  };


  return (
    <div className="loginBody">
      <form onSubmit={handelevent}>
        <div className="MainContainer">
        <img src="https://vyaparwebsiteimages.vypcdn.in/logo1.svg" alt="..."/>
            <h2 className="WelcomeText"> Welcome to vyapar</h2>
           
            <div className="InputContainer">
                <input type="email" className="inputs" placeholder="Email" onChange={e => setEmail(e.target.value)} required/>
                <input type="password" className="inputs" placeholder="Password" onChange={e => setPassword(e.target.value)} required/>
                <select className="inputs" onClick={e => setUser(e.target.value)}>
                    <option value="Admin">Admin</option>
                    <option value="User">User</option>
                 </select>
            </div>
            <div className="ButtonContainer">
                <button className="buttons" type="submit"><h3 style={{color:"white"}}>LOGIN</h3></button>
            </div>
            <div className="ButtonContainer">
                <button className="buttons"><Link to="./signup.js"><h3 style={{color:"white"}}>SIGNUP</h3></Link></button>
            </div>
            <Link to="/ForgetPassword"><h4 className="ForgotPassword">Forgot Password</h4></Link>         
        </div>
      </form>
    </div>
  );
}
